function rescaling(X) #function for rescaling and get the elements of the matrix between -100 and 100
    m, n = size(X)
    for i = 1:n
        b = maximum(X[:, i])
        l = minimum(X[:, i])
        u = (l + b) / 2
        X[:, i] = X[:, i] - u * ones(m)
        b1 = b - u
        l1 = l - u
        w = b1 - l1
        zb = 1e2
        zl = -1e2
        if b1 > zb || l1 < zl
            for j = 1:m
                X[j, i] = (zb - zl) * (X[j, i] - l1) / (b1 - l1) + zl
            end
        end
    end
    return X
end


function preprocessingdata(A)
    D = CSV.read("datasetRF/$A.tsv", DataFrame)
    D = unique(D)
    s, p = size(D)
    D = Matrix(D)
    D = convert(Matrix{Float64}, D)
            Y = D[:, end]
        D = D[:, 1:end-1]
        s, p = size(D)
        for i = 1:s
            if Y[i] != 1
                Y[i] = -1
            end
        end
    end
    D = rescaling(D)
    g = Int64(round(0.01 * s))
    return D, Y, p, s, g
end

function comparing(P1, Y1, s, g, MCnum, M1, D, nt, st,ℓ,u,B=0)
    rng = MersenneTwister(70)
    S = Vector{Vector{Int32}}(undef,MCnum) 
    if B==1 # sample under simple sample
        for i = 1:MCnum
           S[i] = sample(rng,1:s , g; replace=false) 
        end
    else # sample under Biased sample
        SamplingProb = fill(0.15, s)
        SamplingProb[Y1 .== 1] .= 0.85 # sample under Biased sample
        for i = 1:MCnum
            S[i] = StatsBase.wsample(rng, 1:s, SamplingProb, g; replace=false)
        end
    end
    TP1 = zeros(MCnum)
    TP2 = zeros(MCnum)
    FP1 = zeros(MCnum)
    FP2 = zeros(MCnum)
    TN1 = zeros(MCnum)
    TN2 = zeros(MCnum)
    FN1 = zeros(MCnum)
    FN2 = zeros(MCnum)
    TP1u = zeros(MCnum)
    TP2u = zeros(MCnum)
    TP3u = zeros(MCnum)
    FP1u = zeros(MCnum)
    FP2u = zeros(MCnum)
    FP3u = zeros(MCnum)
    TN1u = zeros(MCnum)
    TN2u = zeros(MCnum)
    TN3u = zeros(MCnum)
    FN1u = zeros(MCnum)
    FN2u = zeros(MCnum)
    FN3u = zeros(MCnum)
    f1 = zeros(MCnum)
    f2 = zeros(MCnum)
    f3 = zeros(MCnum)
    ti1 = zeros(MCnum)
    ti2 = zeros(MCnum)
    ti3 = zeros(MCnum)
    GAP1 = zeros(MCnum)
    GAP2 = zeros(MCnum)
    ALPHA = zeros(nt, MCnum)
    ALPHA2 = zeros(nt, MCnum)
    for i = 1:MCnum
        X = P1[S[i], :]
        y = Y1[S[i]]
        k = sort(S[i])
        H = deleteat!(collect(1:s), k)
        yA = findall(==(1), y)
        yB = findall(==(-1), y)
        AX = X[yA, :]
        BX = X[yB, :]
        y = [y[yA]; y[yB]]
        X1 = P1[H, :]
        y1 = Y1[H]
        τA = 0
        τB = 0
        for L = 1:s-g
            if y1[L] == 1
                τA += 1
            else
                τB += 1
            end
        end
        ma = size(AX, 1)
        XAB = [AX; BX]
        t = 0
        

        t2 = @elapsed H2 = RandomForest(XAB, X1, ma, nt, st)
        ti1[i] = t2
        z1 = round.(mean(H2, dims=2))
        for j = 1:s-g
            ac = z1[j]
            if ac > 0.5
                if y1[j] == 1
                    TP1u[i] += 1
                else
                    FP1u[i] += 1
                end
            else
                if y1[j] == -1
                    TN1u[i] += 1
                else
                    FN1u[i] += 1
                end
            end
        end
   
        for AA = 1:length(H2)
            if H2[AA] == 0
                H2[AA] = -1
            end
        end
      
        t3 = @elapsed classF,α,fun,gap =  RFpreprocessingmain(H2,τA,ℓ,u,1,7200)
        f2[i] = fun
        ALPHA[:,i] = α
        GAP1[i] = gap
        ti2[i] = t2 + t3
        for j = 1:g

            if y[j] == 1
                TP2[i] += 1
            else
                TN2[i] += 1
            end
        end
        for j = 1:s-g
            ac = classF[j]
            if ac > 0
                if y1[j] == 1
                    TP2u[i] += 1
                else
                    FP2u[i] += 1
                end
            else
                if y1[j] == -1
                    TN2u[i] += 1
                else
                    FN2u[i] += 1
                end
            end
        end
        t3 = @elapsed classF,α,fun,gap =  classRF(H2,τA,ℓ,u,7200)
        f3[i] = fun
        ALPHA2[:,i] = α
        ti3[i] = t2 + t3
        GAP2[i] = gap
       
            for j = 1 : s-g
                ac = classF[j]
                if ac >= 0.5
                    if y1[j] == 1
                        TP3u[i] += 1
                    else
                        FP3u[i] += 1
                    end
                else
                    if y1[j] == -1
                        TN3u[i] += 1
                    else
                        FN3u[i] += 1
                    end
                end
            end
       
    
        println("comparing $i finished")
    end
    FUN = [f1 f2 f3]
    CSV.write("results/$B/functionvalue$D.csv", DataFrame(FUN, :auto))
    CSV.write("results/$B/ALPHA$D.csv", DataFrame(ALPHA, :auto))
    CSV.write("results/$B/ALPHA2$D.csv", DataFrame(ALPHA2, :auto))
    GAP = [GAP1 GAP2]
    CSV.write("results/$B/GAP$D.csv", DataFrame(GAP, :auto))
    TP = [TP1 TP2 TP1u TP2u TP3u]
    CSV.write("results/$B/TruePositive$D.csv", DataFrame(TP, :auto))

    TN = [TN1 TN2 TN1u TN2u TN3u]
    CSV.write("results/$B/TrueNegative$D.csv", DataFrame(TN, :auto))

    FP = [FP1 FP2 FP1u FP2u FP3u]
    CSV.write("results/$B/FalsePositive$D.csv", DataFrame(FP, :auto))

    FN = [FN1 FN2 FN1u FN2u FN3u]
    CSV.write("results/$B/FalseNegative$D.csv", DataFrame(FN, :auto))

    TIME = [ti1 ti2 ti3]
    CSV.write("results/$B/TIMEs$D.csv", DataFrame(TIME, :auto))
    
end



function running(i,MCnum)
    P1,Y1,M1,s,g = preprocessingdata(i)
     D = i
     nt = 20
     st = Int64(round(0.2 * g))
     comparing(P1, Y1, s, g, MCnum, M1, D, nt, st,1,100)
    
end
